import { GLOBAL_TYPES } from "../actions/globalType"

const initialState = {
    tasks: []
}

const authReducer = (state = initialState, action) => {
    switch (action.type) {
        case G.CREATE_POST:
            return {
                ...state,
                posts: [action.payload, ...state.posts]
            }
        default:
            return state
    }
}

export default authReducer
